import React, { useState, useEffect, Component } from "react";
import { io } from "socket.io-client";
import backendLink from "../settings";

const MessageScreen = ({ token }) => {
  function sendMessage(message, e) {
    e.preventDefault();
    if (message != "") {
      fetch(backendLink + "/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
          token: token,
        },
        body: new URLSearchParams({
          message: message,
        }),
      });
      setMessage("");
    }
  }
  const scrollToBottom = () => {
    if (messagesEnd != null) messagesEnd.scrollIntoView({ behavior: "smooth" });
  };
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [messagesEnd, setMessagesEnd] = useState();
  const [socket, setSocket] = useState(null);
  useEffect(() => {
    setSocket(io(backendLink, { auth: { token } }));
    fetch(backendLink + "/messages", {
      method: "GET",
      headers: { token: token },
    })
      .then((res) => res.json())
      .then((data) => setMessages(data));
  }, []);

  useEffect(
    () => {
      if (socket != null) {
        socket.on("message", (data) => {
          setMessages([...messages, data]);
        });
      }
    },
    [socket, messages],
    scrollToBottom()
  );

  return (
    <div
      className={"container d-flex flex-column align-items-center"}
      style={{
        background: "var(--bs-indigo)",
        width: "369px",
        paddingLeft: "0px",
        paddingRight: "2px",
        marginTop: "71px",
        paddingTop: "10px",
      }}
    >
      <ul
        className={"list-group"}
        style={{
          width: "100%",
          height: "550px",
          background: "rgba(0,0,0,0)",
          color: "var(--bs-dark)",
        }}
      >
        {messages.map((message, i) => (
          <li
            key={{ message_ } + i.toString()}
            className={"list-group-item d-flex align-items-end"}
            style={{
              paddingRight: "14px",
              marginBottom: "8px",
              borderRadius: "5px",
              paddingLeft: "6px",
              marginTop: "16px",
            }}
          >
            <div style={{ width: "35px" }}>
              <p
                style={{
                  height: "26px",
                  width: "152px",
                  position: "absolute",
                  color: "var(--bs-white)",
                  marginLeft: "207px",
                  marginTop: "-34px",
                  paddingTop: "0px",
                  marginBottom: 0,
                  marginRight: 0,
                  wordWrap: "break-word",
                  textAlign: "right",
                  overflow: "hidden",
                }}
              >
                {message.username}
              </p>
              <div
                className={
                  "bs-icon-sm bs-icon-rounded bs-icon-primary d-flex flex-shrink-0 justify-content-center align-items-center d-inline-block bs-icon"
                }
                style={{ paddingBottom: "0px", marginBottom: "0px" }}
              ></div>
            </div>
            <div
              className={"px-2"}
              style={{
                paddingLeft: "15px",
                marginLeft: "7px",
                overflow: "visible",
                width: "90%",
              }}
            >
              <h5 className={"mb-0 mt-1"} style={{ wordWrap: "break-word" }}>
                {message.message}
              </h5>
            </div>
          </li>
        ))}
        <div
          style={{ float: "left", clear: "both" }}
          ref={(el) => {
            setMessagesEnd(el);
          }}
        />
      </ul>
      <form
        onSubmit={(e) => sendMessage(message, e)}
        style={{
          minWidth: "100%",
          width: "100%",
          background: "rgba(0,0,0,0)",
          marginTop: "1px",
          display: "flex",
        }}
      >
        <input
          autoFocus
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className={"form-control"}
          type={"text"}
          style={{ display: "block", outline: "none" }}
        />
        <button
          className={"btn btn-primary"}
          type={"submit"}
          style={{ display: "block" }}
        >
          &gt;
        </button>
      </form>
    </div>
  );
};

export default MessageScreen;
