import React, { useState, useEffect, Component } from "react";
const MessageScreen = ({ socket, token }) => {
  function sendMessage(message,e) {
    e.preventDefault()
    if (message != "") {
      fetch("http://localhost:5000/messages", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: user, message: message }),
      });
      setMessage("");
    }
  }
  const scrollToBottom = () => {
    if (messagesEnd != null)
        messagesEnd.scrollIntoView({ behavior: "smooth" });
  }
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [messagesEnd, setMessagesEnd] = useState();
  useEffect(() => {
    fetch("http://localhost:5000/messages", { method: "GET" })
      .then((res) => res.json())
      .then((data) => setMessages(data));
  }, []);
  useEffect(() => {
    socket.on("message", (data) => {
      setMessages([...messages, data]);
    });
  }, [socket, messages],scrollToBottom());
  return (
    <>
      <div style={styles.messages}>
        {messages.map((message, i) => (
          <div key={"message_" + i.toString()} style={styles.message}>
            {message.name} : {message.message}
          </div>
        ))}
        <div style={{ float:"left", clear: "both" }}
             ref={(el) => { setMessagesEnd(el); }} />

      </div>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          width: "90%",
        }}
        onSubmit={(e) => sendMessage(message,e)}
      >
        <input
          autoFocus
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          type="text"
          style={styles.input}
        />
        <button type="submit" style={styles.send}>
          Send
        </button>
      </form>
    </>
  );
};

const styles = {
  main: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    background: "linear-gradient(to right, #5f2c82, #49a09d)",
  },
  form: {
    height: "40rem",
    width: "30rem",
    background: "rgba(0, 0, 0, 0.3)",
    borderRadius: "0.5rem",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  input: {
    background: "#00000069",
    color: "white",
    padding: ".4rem",
    display: "block",
    width: "80%",
    marginBottom: "1rem",
    fontSize: "1.2rem",
  },
  send: {
    fontSize: "1.2rem",
    display: "block",
    background: "rgb(165 45 255)",
    borderRadius: "0.5rem",
    padding: ".4rem",
    width: "50%",
    cursor: "pointer",
  },
  messages: {
    height: "25rem",
    width: "80%",
    padding: "0.8rem",
    overflowY: "scroll",
    background: "#00000069",
    marginBottom: "1rem",
  },
  message: {
    color: "white",
    fontSize: "1.2rem",
  },
};

export default MessageScreen;
